<script setup>
import { ref, onMounted } from "vue";
import {router, Head} from "@inertiajs/vue3"
import Layout from "../Layout/Layout.vue";
const walletAddress = ref(localStorage.getItem("walletAddress") || "");
const walletConnected = ref(localStorage.getItem("walletConnected") === "true");
const loading = ref(false);
const notificationVisible = ref(false);

// Получить провайдер Phantom
const getProvider = () => {
    if ("phantom" in window) {
        const provider = window.phantom?.solana;
        if (provider?.isPhantom) {
            return provider;
        }
    }
    window.open("https://phantom.app/", "_blank");
    return null;
};

const connectWallet = async () => {
    console.log("Кнопка нажата, пытаюсь подключить Phantom...");

    const provider = getProvider();
    if (!provider) {
        alert("Phantom wallet не установлен.");
        return;
    }

    try {
        loading.value = true;

        const response = await provider.connect();
        walletAddress.value = response.publicKey.toString();
        walletConnected.value = true;

        localStorage.setItem("walletAddress", walletAddress.value);
        localStorage.setItem("walletConnected", "true");

        showNotification();

        router.post(
            "/user/connect",
            { walletAddress: walletAddress.value },
            {
                onSuccess: () => {
                    console.log("Адрес сохранён, переход на dashboard");
                    // тут редирект сделает сам Laravel (redirect()->route('dashboard'))
                },
                onError: (errors) => {
                    console.error("Ошибка при сохранении:", errors);
                    alert("Произошла ошибка при подключении кошелька.");
                },
            }
        );
    } catch (err) {
        console.error("Ошибка подключения Phantom:", err);
        alert("Не удалось подключить кошелёк.");
    } finally {
        loading.value = false;
    }
};
// Уведомления
const showNotification = () => {
    notificationVisible.value = true;
    setTimeout(() => (notificationVisible.value = false), 5000);
};

onMounted(() => {
    if (walletConnected.value && walletAddress.value) {
        // Авто-восстановление состояния кнопки
    }
});
</script>
